import styled from "styled-components";

export const MyButtons = styled.button`
    border: none;
    color: #fff;
    width: 28vh;
    height: 10vh;
    border-radius: 30px;

${({ ativo }) =>
  ativo
    ? `
        color: #ffffff;
        background: #00bfa5;
      `
    : `
        color: #607d8b;
        background: #ffffff;
      `}

&:focus {
  outline: none;
}
`;
