import styled from "styled-components";

export const Button = styled.div`
    width: 100%;
    height: 50vh;

    background: #FFFFFF 0% 0% no-repeat padding-box;
    box-shadow: 0px 3px 6px #00000029;
    border-radius: 25px;
    opacity: 1;

`

export const MyButtons = styled.button`
    border: 1px solid;
    color: #fff;
    width: 28vh;
    height: 10vh;
    border-radius: 30px;

${({ ativo }) =>
  ativo
    ? `
        color: #ffffff;
        background: #00bfa5;
      `
    : `
        color: #607d8b;
        background: #ffffff;
      `}

&:focus {
  outline: none;
}
`;
